pragma solidity ^0.8.18;

import "@chainlink/contracts/src/v0.8/ChainlinkClient.sol";
import "@chainlink/contracts/src/v0.8/ConfirmedOwner.sol";

import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

contract SwapHelper is AccessControl, ChainlinkClient, ConfirmedOwner {
    using Address for address;
    using Address for address payable;
    using SafeERC20 for IERC20;

    bytes32 public constant SWAP_AUTHORIZED_ROLE = keccak256("SWAP_AUTHORIZED_ROLE");

    address public immutable aggregationRouter;

    constructor(
        address _aggregationRouter,
        address[] memory authorizedToSwap
    )
        ConfirmedOwner(_msgSender())
    {
        aggregationRouter = _aggregationRouter;
        for (uint8 i = 0; i < authorizedToSwap.length; i++) {
            _grantRole(SWAP_AUTHORIZED_ROLE, authorizedToSwap[uint256(i)]);
        }
    }

    function exactInput() external view returns(uint256) {

    }

    function exactOutput() external view returns(uint256) {
        
    }

    function swap(
        address[] memory path,
        uint256[] memory slippages
    ) external payable onlyRole(SWAP_AUTHORIZED_ROLE) returns(uint256) {
        
    }
}","keccak256":"0xd20e8808c71f25baf0fe29d917e65efe9a50290d620c780eb1dbb00ec210ded3","license":"MIT"}},"version":1}
